mapper.select:
select  deptname, deptno
from TBL_DEPT
where upper_deptno = #{deptno}

select empno, name
from TBL_EMPLOYEES
where STATUS='재직' AND fk_deptno = #{deptno} AND empno != #{empno}

select name, substr(name, -2, 2) as nickname , deptname, position, profile_color, pk_msgno , empno, origin_msgno, subject, content, view_status ,to_char(WRITEDATE , 'yyyy-mm-dd hh24:mi:ss' ) as writedate, having_attach from ( select rownum as rno, name , deptname, position, profile_color, pk_msgno , empno, origin_msgno, subject, content, view_status , writedate, having_attach from ( select PK_MSGNO , FK_RECIPIENTNO, ORIGIN_MSGNO, SUBJECT, CONTENT, VIEW_STATUS, WRITEDATE, having_attach from tbl_messenger where FK_SENDERNO = #{empno} order by WRITEDATE desc ) join ( select empno, NAME, DEPTNAME, POSITION, PROFILE_COLOR from TBL_EMPLOYEES M join TBL_DEPT D on M.fk_deptno = D.deptno
) on FK_RECIPIENTNO = empno
where lower(name) like '%' || lower(#{searchWord}) || '%' OR lower(subject) like '%' || lower(#{searchWord}) ||'%' OR lower(content) like '%' || lower(#{searchWord}) || '%'
) where rno between #{startRno} and #{endRno}

select name, substr(name, -2, 2) as nickname , deptname, position, profile_color, pk_msgno , empno, origin_msgno, subject, content, view_status ,to_char(WRITEDATE , 'yyyy-mm-dd hh24:mi:ss' ) as writedate, having_attach from ( select rownum as rno, name , deptname, position, profile_color, pk_msgno , empno, origin_msgno, subject, content, view_status , writedate, having_attach from ( select PK_MSGNO , FK_RECIPIENTNO, ORIGIN_MSGNO, SUBJECT, CONTENT, VIEW_STATUS, WRITEDATE, having_attach, FK_SENDERNO from tbl_messenger where FK_RECIPIENTNO = #{empno} order by WRITEDATE desc ) join ( select empno, NAME, DEPTNAME, POSITION, PROFILE_COLOR from TBL_EMPLOYEES M join TBL_DEPT D on M.fk_deptno = D.deptno
) on FK_SENDERNO = empno
where lower(name) like '%' || lower(#{searchWord}) || '%' OR lower(subject) like '%' || lower(#{searchWord}) ||'%' OR lower(content) like '%' || lower(#{searchWord}) || '%'
) where rno between #{startRno} and #{endRno}

select pk_fileno, fileName, orgFilename, fileSize
from tbl_messenger_file
where fk_msgno = #{group_msgno}

select GROUP_MSGNO, HAVING_ATTACH
from tbl_messenger
where PK_MSGNO = #{msgno}

select count(PK_MSGNO) from ( select PK_MSGNO , FK_RECIPIENTNO, ORIGIN_MSGNO, SUBJECT, CONTENT, VIEW_STATUS, WRITEDATE, having_attach from tbl_messenger where FK_SENDERNO =#{empno} order by WRITEDATE desc ) join ( select empno, NAME, DEPTNAME, POSITION, PROFILE_COLOR from TBL_EMPLOYEES M join TBL_DEPT D on M.fk_deptno = D.deptno
) on FK_RECIPIENTNO = empno
where lower(name) like '%' || lower(#{searchWord}) || '%' OR lower(subject) like '%' || lower(#{searchWord}) ||'%' OR lower(content) like '%' || lower(#{searchWord}) || '%'

select count(PK_MSGNO) from ( select PK_MSGNO , FK_RECIPIENTNO, ORIGIN_MSGNO, SUBJECT, CONTENT, VIEW_STATUS, WRITEDATE, having_attach, FK_SENDERNO from tbl_messenger where FK_RECIPIENTNO =#{empno} order by WRITEDATE desc ) join ( select empno, NAME, DEPTNAME, POSITION, PROFILE_COLOR from TBL_EMPLOYEES M join TBL_DEPT D on M.fk_deptno = D.deptno
) on FK_SENDERNO = empno
where lower(name) like '%' || lower(#{searchWord}) || '%' OR lower(subject) like '%' || lower(#{searchWord}) ||'%' OR lower(content) like '%' || lower(#{searchWord}) || '%'

select name
from TBL_EMPLOYEES
where empno = #{empno}
